var countDownDate=new Date("March 28, 2020 00:00:00").getTime();var x=setInterval(function(){var b=new Date().getTime();var f=countDownDate-b;var e=Math.floor(f/(1000*60*60*24));var a=Math.floor((f%(1000*60*60*24))/(1000*60*60));var c=Math.floor((f%(1000*60*60))/(1000*60));var d=Math.floor((f%(1000*60))/1000);document.getElementById("timer").innerHTML=e+"d "+a+"h "+c+"m "+d+"s ";if(f<0){clearInterval(x);document.getElementById("demo").innerHTML="EXPIRED"}},1000);